mui.init();var vm=new Vue({el:"#root",data:{guid:'',author:'',items:[]}});mui.plusReady(function(){window.addEventListener('get_detail',function(event){vm.guid=event.detail.guid;var id=event.detail.id;vm.author=event.detail.author;mui.ajax("http://127.0.0.1:10261/itsm/rest/api/v2/itsm/tickets/query?userId=admin&processId="+id,{dataType:'json',type:'get',success:function(res){vm.items.push(res.ticket)},error:function(xhr,type,errorThrown){alert('错误'+xhr.status+type+errorThrown)}})});mui.back=function(){plus.webview.currentWebview().hide("auto",300);var self=plus.webview.currentWebview();self.addEventListener("hide",function(e){vm.guid='',vm.author='',vm.items=[]},false)}});